\begin{equation*}R_{{\mathrm{Sites}}_{{\mathrm{i}}}}({\beta},f_{{\mathrm{i}}},{\omega})=\frac{e_{{\mathrm{z}}}^{2}(f_{{\mathrm{i}}}K_{{\mathrm{z}}}({\omega})+\displaystyle\frac{1-f_{{\mathrm{i}}}}{3})+e_{{\mathrm{x}}}^{2}(f_{{\mathrm{i}}}K_{{\mathrm{x}}}({\omega})+\displaystyle\frac{1-f_{{\mathrm{i}}}}{3})}{e_{{\mathrm{y}}}^{2}(f_{{\mathrm{i}}}K_{{\mathrm{y}}}({\omega})+\displaystyle\frac{1-f_{{\mathrm{i}}}}{3})}.\end{equation*}\end{document}
